update_prefix = "UPDATE products SET description = \'{desc}\' WHERE title = \'{newname}\';"
category = ["men", "women"]
for cat in category:
    if cat == "men":
        prod_num = 17
        for i in range(prod_num):
            menGarmentDescriptions[i] = menGarmentDescriptions[i].replace("'", "\\'")
            print(update_prefix.format(desc = menGarmentDescriptions[i], newname=menGarmentNames[i]))
    else:
        prod_num = 15
        for i in range(prod_num):
            womenGarmentDescriptions[i] = womenGarmentDescriptions[i].replace("'", "\\'")
            print(update_prefix.format(desc = womenGarmentDescriptions[i], newname=womenGarmentNames[i]))
